import React, {useState} from 'react'
import {Link, useHistory} from 'react-router-dom'

import axios from 'axios'
import {showErrMsg, showSuccessMsg} from '../utils/notification/Notification'

const initialState = {
    email: '',
    password: '',
    err: '',
    success: ''
}



function Login(){
    const [user, setUser] = useState(initialState)
    const {email, password, err, success} = user

    const handleChangeInput = e => {
        const {name, value} = e.target
        setUser({...user, [name]:value, err: '', success: ''})
    }

    const handleSubmit = async e => {
        e.preventDefault()
        try {
            const res = await axios.post('/user/login', {email, password})

            console.log(res)

        } catch (err) {
            err.response.data.msg && 
            setUser({...user, err: err.response.data.msg, success: ''})
        }
    }


    return (
            <div className="login_page">
                <h2>Login</h2>
                {err && showErrMsg(err)}
                {success && showSuccessMsg(success)}

                <form onSubmit={handleSubmit}>
                    <div>
                        <label htmlFor="email">Email Address</label>
                        <input class="form-control" type="text" placeholder="Enter email address" id="email"
                        value={email} name="email" onChange={handleChangeInput} />
                    </div>

                    <div>
                        <label htmlFor="password">Password</label>
                        <input class="form-control" type="password" placeholder="Enter password" id="password"
                        value={password} name="password" onChange={handleChangeInput}/>
                    </div>

                    <div className="row_custom row">
                        <button class="btn text-white bg-dark mb-2" type="submit">Login</button>
                        <Link to="/forgot_password">Forgot your password?</Link>
                    </div>
                </form>

                <div className="hr">Or Login With</div>

                
            <div className="social">



            </div>

                <p>New Customer? <Link to="/register">Register</Link></p>
            </div>
       
    )
}
export default Login
