import React, {useContext,useEffect} from 'react'
import { Row, Col } from 'antd';
import {Link} from 'react-router-dom'
import {GlobalState} from '../../../../GlobalState'
import Aos from 'aos'
import "aos/dist/aos.css"

function PetItem({pet}) {
    const state = useContext(GlobalState)
    const [statuses] = state.statusesAPI.statuses
    const [types] = state.typesAPI.types
    useEffect(()=>{
        Aos.init({duration: 2000}); 
    })
    return (
            <Col xs={24} sm={24} md={12} lg={12} xl={6} data-aos="fade-up" data-aos-easing="ease-out-cubic">
                <Link to={`information/${pet._id}`}>
                <div className="rounded overflow-hidden shadow-lg transition duration-700 ease-in-out ... transform hover:scale-110 motion-reduce:transform-none ...">
                    <Link to={`information/${pet._id}`}><img  className="w-full" src={pet.images.url} alt="" /></Link>
                    <div className="px-6 py-4">
                        <span className="inline-block bg-gray-200 rounded-full px-3 py-1 text-xl font-semibold text-gray-700 mr-2">#travel</span>
                        <span className="inline-block bg-gray-200 rounded-full px-3 py-1 text-xl font-semibold text-gray-700">#winter</span>
                    </div>
                    <div className="px-6 py-4">
                        <div className="font-bold text-4xl mb-2">The Coldest Sunset</div>
                        <p className="text-gray-700 text-xl">
                        Lorem ipsum dolor sit amet, consectetur adipisicing elit. Voluptatibus quia, nulla! Maiores et perferendis eaque, exercitationem praesentium nihil.
                        </p>
                    </div>
                </div>
                </Link>
            </Col>
    );
}

export default PetItem;
