import React, {useContext,useEffect} from 'react'
import { Row, Col } from 'antd';
import {Link} from 'react-router-dom'
import {GlobalState} from '../../../../GlobalState'
import Aos from 'aos'
import "aos/dist/aos.css"

function PetItem({pet}) {
    const state = useContext(GlobalState)
    const [statuses] = state.statusesAPI.statuses
    const [types] = state.typesAPI.types
    useEffect(()=>{
        Aos.init({duration: 2000}); 
    })
    return (
            <Col xs={24} sm={24} md={12} lg={12} xl={6} data-aos="fade-up" data-aos-easing="ease-out-cubic">
                <div className="rounded overflow-hidden shadow-lg">
  <img  className="w-full transition duration-700 ease-in-out ... transform hover:scale-110 motion-reduce:transform-none ..." src="https://source.unsplash.com/random/1600x900" alt="Sunset in the mountains" />
  <div className="px-6 py-4">
    <div className="font-bold text-xl mb-2">The Coldest Sunset</div>
    <p className="text-gray-700 text-base">
      Lorem ipsum dolor sit amet, consectetur adipisicing elit. Voluptatibus quia, nulla! Maiores et perferendis eaque, exercitationem praesentium nihil.
    </p>
  </div>
  <div className="px-6 py-4">
    <span className="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2">
      #photography
    </span>
    <span className="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2">#travel</span>
    <span className="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700">#winter</span>
  </div>
</div>

            </Col>
    );
}

export default PetItem;
