import React,{useContext,useSelector} from 'react';
import BreadCrumb from '../BreadCumb'
import {GlobalState} from '../../../GlobalState'
import { Row, Col } from 'antd';
import PostItem from '../posts/Posts'
import { htmlToText } from 'html-to-text';
import { Link } from 'react-router-dom';
import moment from 'moment';
import Aos from 'aos'
import "aos/dist/aos.css"

function Posts() {
    const state = useContext(GlobalState)
    const [posts, setPosts] = state.myPostsAPI.posts
    const [topics] = state.topicsAPI.topics

    console.log('state all post.js')
    console.log(posts)
    return (
        <>
            <div className="pt-6 pb-12 bg-gray-100">
                <div className="container">
                    <Row gutter={[16, 16]} >
                        <Col xs={{span: 24}} sm={{span: 24}} md={{span: 24}} lg={{span: 18}} lx={{span: 18}}>
                        <h1 className='mt-20 text-5xl'>Tin Tức</h1>
                        {
                            posts.map((post) => (
                                <div class="w-100 lg:w-4/5 mx-auto flex flex-col">
                                  <div v-for="card in cards" className="flex flex-col md:flex-row overflow-hidden
                                  bg-white rounded-lg shadow-xl  mt-4 w-100 mx-2">
        {/* media */}
        <div className="h-64 w-auto md:w-1/2">
          <img className="inset-0 h-full w-full object-cover object-center" src="card.img" />
        </div>
        {/* content */}
        <div className="w-full py-4 px-6 text-gray-800 flex flex-col justify-between">
          <h3 className="font-semibold text-lg leading-tight truncate">{'{'}{'{'} card.title {'}'}{'}'}</h3>
          <p className="mt-2">
            {'{'}{'{'} card.excerpt {'}'}{'}'}
          </p>
          <p className="text-sm text-gray-700 uppercase tracking-wide font-semibold mt-2">
            {'{'}{'{'} card.author {'}'}{'}'} • {'{'}{'{'} card.date {'}'}{'}'}
          </p>
        </div>
      </div>{/*/ card*/}
                                </div>

                            ))
                        }
                        </Col>
                    </Row>
                </div>
            </div>
        </>
    );
}

export default Posts;
