import React, {useContext} from 'react';
import Slider from "react-slick";
import { Card } from 'antd';
import {Link} from 'react-router-dom'
import 'antd/dist/antd.css';
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import {GlobalState} from '../../../GlobalState'

function Case(){
  const state = useContext(GlobalState)
  const [pets_slider, setPetsSlider] = state.petsAPI.pets_slider

        const slider_adopt = {
            dots: false,
            infinite: true,
            speed: 500,
            slidesToShow: 4,
            slidesToScroll: 4,
            responsive: [
                {
                  breakpoint: 1024,
                  settings: {
                    slidesToShow: 3,
                    slidesToScroll: 3,
                    infinite: true,
                    dots: false
                  }
                },
                {
                    breakpoint: 768,
                    settings: {
                      slidesToShow: 2,
                      slidesToScroll: 2,
                      infinite: true,
                      dots: false
                    }
                  },
                {
                  breakpoint: 600,
                  settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                    initialSlide: 2
                  }
                },
                {
                  breakpoint: 480,
                  settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1
                  }
                }
              ]
        };
        return (
            <section className="adoptions">
                <div className="container">
                    <h1 className="section-heading">Adoption</h1>
                        <Slider className="slider-adoption" {...slider_adopt}>
                          {
                            pets_slider.map((pet)=>(
                              <div className="img-card bg-white">
                                <img className="img" src={pet.images.url} alt="" />
                                <div class="card-body">
                                    <Link to={`adoption/${pet._id}`}><div className="card-title text-yellow-700">{pet.name}</div></Link>
                                    <div className="card-text">{pet.sex}</div>
                                </div>
                            </div>
                            ))
                          }
                       
                        </Slider>
                    </div>

            </section>
        );
}


export default Case;
