import React,{useContext,useSelector} from 'react';
import BreadCrumb from '../BreadCumb'
import {GlobalState} from '../../../GlobalState'
import { Row, Col } from 'antd';
import PostItem from '../posts/Posts'
import { htmlToText } from 'html-to-text';
import { Link } from 'react-router-dom';
import moment from 'moment';

function Posts() {
    const state = useContext(GlobalState)
    const [posts, setPosts] = state.myPostsAPI.posts

    console.log('state all post.js')
    console.log(posts)
    return (
        <>
        <div className='container' style={{marginTop:'100px'}}>
            <div class="block lg:flex lg:space-x-2 px-2 lg:p-0 mt-10 mb-10">
                <div class="w-full lg:w-2/3" >
                {
                    posts.map((post) => (
                        
                            <div key={post._id} className="block rounded w-full lg:flex mb-10">
                                <div className="h-48 lg:h-auto lg:w-48 flex-none bg-cover rounded-t lg:rounded-t-none lg:rounded-l text-center overflow-hidden" style={{ backgroundImage: `url(${post.images.url})` }} title="Woman holding a mug"></div>
                                <div className="border-r border-b border-l border-grey-light lg:border-l-0 lg:border-t lg:border-grey-light bg-white rounded-b lg:rounded-b-none lg:rounded-r p-4 flex flex-col justify-between leading-normal">
                                    <div className="mb-8">
                                        <div className="text-black font-bold text-4xl mb-2">{post.title}</div>
                                        <p className="text-grey-darker text-xl">{post.description}</p>
                                    </div>
                                    <div className="flex items-center">
                                        <img className="w-15 h-15 rounded-full mr-4" src={post.userAvatar} alt="" />
                                        <div className="text-sm">
                                        <p className="text-black leading-none text-base">{post.userName}</p>
                                        <p className="text-grey-dark text-base">{moment(post.updatedAt).format('MM/DD/YYYY')}</p>
                                        </div>
                                    </div>
                                </div>
                            </div>
                       
                    ))
                }
                </div>
                <div class="w-full lg:w-1/3 px-3"></div>
            </div>
        </div>
        </>
    );
}

export default Posts;
