import React from "react";
import { useLocation, Link } from "react-router-dom";
import { Breadcrumb,Tag } from "antd";
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import "./breadcumb.css";
const BreadCrumb = () => {
  const token = localStorage.getItem('access_token')
  const location = useLocation();
  const breadcrumbNameMap = {
    '/login': 'Login',
    '/register': 'Register',
    '/user/activate/:activation_token': 'Activate',
    '/forgot_password': 'Forgot Password',
    '/adoption': 'Adoption',
    '/stories' : 'Stories'
  };

  const items = [
    { to: '/', label: 'Home' },
    { to: `/user/reset/${token}`, label: 'Reset' },
    { to: '/adoption', label: 'Adoption' }
  ]

  const extra= items.map((item,index) => {
    console.log('item.to',item.to)
    const path = item.to.split("/").filter((item) => item);
    const url = `/${path.slice(0, index + 1).join('/')}`;
    console.log('path',path)
    console.log('url1',url)
  });
  
  const { pathname } = location;
  console.log('location',location)

  const pathnames = pathname.split("/").filter((item) => item);
  const extraBreadcrumbItems = pathnames.map((_, index) => {
    const url = `/${pathnames.slice(0, index + 1).join('/')}`;
    console.log('url',url)
    return (
      <>
     {
       items.map((item) => (
        <Breadcrumb.Item key={item.to}>
          {/* {url===item.to?<Link key={item.label} to={item.to}>{item.label}</Link>:''} */}

          {item.find(_=>_.to ===url)&&<Link key={item.label} to={item.to}>{item.label}</Link>||""}
        </Breadcrumb.Item>
       ))
     }
      </>
    );
  });


  const breadcrumbItems = [
    <Breadcrumb.Item key="home">
      <Link to="/">Home</Link>
    </Breadcrumb.Item>,
  ].concat(extraBreadcrumbItems);

  return <>
  <div className="breadcumb">
  <div className="mybread slide">
    <div className="container bread_txt">
      <Tag color="#ce7852"><Breadcrumb>{breadcrumbItems}</Breadcrumb></Tag>
    </div>
  </div>
</div>
  </>;
};

export default BreadCrumb;
