import React, {createContext, useState, useEffect} from 'react'
import{BrowserRouter as Router} from 'react-router-dom'
import {DataProvider} from './GlobalState'
import {Helmet} from "react-helmet";
import Header from './components/headers/Header'
import MainPages from './components/mainpages/Pages'
function App() {
  useEffect(() => {
    const script = document.createElement('script');
    script.src = 'https://mnat261999.github.io/js/vendor/modernizr-3.5.0.min.js';
    script.async = true;
    document.body.appendChild(script);
    return () => {
      document.body.removeChild(script);
    };
  });

  useEffect(() => {
    const script = document.createElement('script');
    script.src = 'https://mnat261999.github.io/js/vendor/jquery-3.2.1.min.js';
    script.async = true;
    document.body.appendChild(script);
    return () => {
      document.body.removeChild(script);
    };
  });

  useEffect(() => {
    const script = document.createElement('script');
    script.src = 'https://mnat261999.github.io/js/popper.min.js';
    script.async = true;
    document.body.appendChild(script);
    return () => {
      document.body.removeChild(script);
    };
  });

  useEffect(() => {
    const script = document.createElement('script');
    script.src = 'https://mnat261999.github.io/js/bootstrap.min.js';
    script.async = true;
    document.body.appendChild(script);
    return () => {
      document.body.removeChild(script);
    };
  });

  useEffect(() => {
    const script = document.createElement('script');
    script.src = 'https://mnat261999.github.io/js/plugins.js';
    script.async = true;
    document.body.appendChild(script);
    return () => {
      document.body.removeChild(script);
    };
  });

  useEffect(() => {
    const script = document.createElement('script');
    script.src = 'https://mnat261999.github.io/js/active.js';
    script.async = true;
    document.body.appendChild(script);
    return () => {
      document.body.removeChild(script);
    };
  });
  return (
    <DataProvider>
      <Router>
          <Header/>
          <MainPages />
      </Router>
    </DataProvider>
  );
}

export default App;
