import React, {useState, useContext, useEffect} from 'react'
import axios from 'axios'
import {GlobalState} from '../../../GlobalState'
import Loading from '../utils/loading/Loading'
import {useHistory, useParams} from 'react-router-dom'
import {useSelector} from 'react-redux'
import { Row, Col } from 'antd';
import './css/createProduct.css'
import './css/form.css'

const initialState = {
    pet_code:'',
    name: '',
    type: '',
    color: '',
    weight: '',
    sex: '',
    vaccination: '',
    moreinfor:'',
    status:'',
    _id: ''
}

function CreatePet() {
    const state = useContext(GlobalState)
    const [pet, setPet] = useState(initialState)
    const [types] = state.typesAPI.types
    const [statuses] = state.statusesAPI.statuses
    const [images, setImages] = useState(false)
    const [loading, setLoading] = useState(false)

    const auth = useSelector(state => state.auth)
    const {isAdmin} = auth
    const token = useSelector(state => state.token)

    //const [isAdmin] = state.userAPI.isAdmin
    //const [token] = state.token

    const history = useHistory()
    const param = useParams()

    const [pets] = state.petsAPI.pets
    const [onEdit, setOnEdit] = useState(false)
    const [callback, setCallback] = state.petsAPI.callback

    useEffect(() => {
        if(param.id){
            setOnEdit(true)
            pets.forEach(pet => {
                if(pet._id === param.id) {
                    setPet(pet)
                    setImages(pet.images)
                }
            })
        }else{
            setOnEdit(false)
            setPet(initialState)
            setImages(false)
        }
    }, [param.id, pets])

    const handleUpload = async e =>{
        e.preventDefault()
        try {
            if(!isAdmin) return alert("You're not an admin")
            const file = e.target.files[0]
            
            if(!file) return alert("File not exist.")

            if(file.size > 1024 * 1024) // 1mb
                return alert("Size too large!")

            if(file.type !== 'image/jpeg' && file.type !== 'image/png') // 1mb
                return alert("File format is incorrect.")

            let formData = new FormData()
            formData.append('file', file)

            setLoading(true)
            const res = await axios.post('/api/upload_pet', formData, {
                headers: {'content-type': 'multipart/form-data', Authorization: token}
            })
            setLoading(false)
            setImages(res.data)

        } catch (err) {
            alert(err.response.data.msg)
        }
    }


    return (
        <div>
            
        </div>
    );
}

export default CreatePet;
