import React, { Component } from 'react';
import Slider from "react-slick";
import { Card } from 'antd';
import 'antd/dist/antd.css';



class Case extends Component {
    render() {
        const slider_adopt = {
            dots: true,
            infinite: true,
            speed: 500,
            slidesToShow: 4,
            slidesToScroll: 4,
            responsive: [
                {
                  breakpoint: 1024,
                  settings: {
                    slidesToShow: 3,
                    slidesToScroll: 3,
                    infinite: true,
                    dots: true
                  }
                },
                {
                  breakpoint: 600,
                  settings: {
                    slidesToShow: 2,
                    slidesToScroll: 2,
                    initialSlide: 2
                  }
                },
                {
                  breakpoint: 480,
                  settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1
                  }
                }
              ]
        };
        return (
            <section className="top-products">
                <div className="container">
                    <h1 className="section-heading">Adoption</h1>
                        <Slider className="slider-adoption" {...slider_adopt}>
                            
                        </Slider>
                    </div>
            </section>
        );
    }
}


export default Case;
