import {useState, useEffect,useContext} from 'react'
import axios from 'axios'
import {GlobalState} from '../../../../GlobalState'
import { Row, Col } from 'antd';
import { Select } from 'antd';
import {useHistory, useParams} from 'react-router-dom'
import { PageHeader } from 'antd';
import { Button, Tooltip } from 'antd';
import { createFromIconfontCN } from '@ant-design/icons';

const { Option } = Select;

function SearchPet() {
    const IconFont = createFromIconfontCN({
        scriptUrl: [
          '//at.alicdn.com/t/font_2520839_3xfc6cekias.js', 
          '//at.alicdn.com/t/font_2520839_dpus1tqve4p.js',
          '//at.alicdn.com/t/font_2520839_5ng5lkrrovm.js',
          '//at.alicdn.com/t/font_2520839_zdkkbqi5vp.js',
          '//at.alicdn.com/t/font_2520839_ivqawpasde8.js',
          '//at.alicdn.com/t/font_2520839_wwwjhyp7f.js',
          '//at.alicdn.com/t/font_2520839_ebx4e8n5w3.js',
          '//at.alicdn.com/t/font_2520839_a1a448dfqf.js'
        ],
      });


    const state = useContext(GlobalState)
    const [type, setType] = useState('')
    const [status, setStatus] = useState('')
    const [searchName, setSearchName] = useState('')
    const [statuses,setStatuses] = state.statusesAPI.statuses
    const [types, setTypes] = state.typesAPI.types
    const [pets, setPets] = state.petsAPI.pets

    const handleType = e => {
        console.log('e.target.value type')
        console.log(e.target.value)
        setType(e.target.value)
    }

    const handleStatus = e => {
        console.log('e.target.value status')
        console.log(e.target.value)
        setStatus(e.target.value)
    }

    const handleSearch = e => {
        console.log('e.target.value search')
        console.log(e.target.value)
        setSearchName(e.target.value)
    }

    const handleSubmit = async e => {
        e.preventDefault()

            if(type)
            {
                const res = await axios.get(`/api/pets?${type}`)
                console.log(res)
                setPets(res.data.pets)
            }
            if(status)
            {
                const res = await axios.get(`/api/pets?${status}`)
                console.log(res)
                setPets(res.data.pets)
            }
            if(searchName)
            {
                const res = await axios.get(`/api/pets?keyword=${searchName}`)
                console.log(res)
                setPets(res.data.pets)
            }
            else if(type || status || searchName)
            {
                const res = await axios.get(`/api/pets?${type}&${status}&keyword=${searchName}`)
                console.log(res)
                setPets(res.data.pets)
            }else if(!type && !status && !searchName)
            {
                const res = await axios.get('/api/pets')
                setPets(res.data.pets)
            }
    }

    const handleReset  = e => {
        console.log(type, status, searchName)
        setType('')
        setStatus('')
        setSearchName('')
    }

    return (
        <div className="site-page-header-ghost-wrapper">
            <PageHeader>
                <form>
                    <Row justify="space-between" gutter={[8, 8]} className='mr-b'>
                        <Col xs={24} sm={24} md={24} lg={4} xl={4}>
                            <Button icon={<IconFont type="icondelete"/>}/>
                        </Col>
                    </Row>
                </form>
            </PageHeader>
        </div>
    );
}

export default SearchPet;
