import React, {useState, useContext,useEffect} from 'react'
import {GlobalState} from '../../../GlobalState'
import axios from 'axios'
import {useSelector} from 'react-redux'
import './css/alluser.css'
import './css/category.css'
import {Link} from 'react-router-dom'
import { Row, Col } from 'antd';
import {showSuccessMsg, showErrMsg} from '../utils/notification/Notification'
import { Modal, Button } from 'antd';
import { Input } from 'antd';
import { Tree } from 'antd';
import { Select } from 'antd';

const { Option } = Select;




function Categories() {
    const state = useContext(GlobalState)
    console.log(state)
    const [categories] = state.categoriesAPI.categories
    const [category, setCategory] = useState('')

    const token = useSelector(state => state.token)
    const [callback, setCallback] = state.categoriesAPI.callback
    const [onEdit, setOnEdit] = useState(false)
    const [id, setID] = useState('')

    const [err, setErr] = useState(false)
    const [success, setSuccess] = useState(false)

    const [visible, setVisible] = useState(false)
    const [loading, setLoading] = useState(false)

    const [categoryName, setCategoryName] = useState('')
    const [parentCategoryId, setParentCategoryId] = useState('')

    const [checked, setChecked] = useState([]);
    const [expanded, setExpanded] = useState([]);

    const [checkedArray, setCheckedArray] = useState([]);
    const [expandedArray, setExpandedArray] = useState([]);


    const onExpand = (expandedKeysValue) => {
        console.log('onExpand', expandedKeysValue); // if not set autoExpandParent to false, if children expanded, parent can not collapse.
        // or, you can remove all expanded children keys.
        setExpanded(expandedKeysValue)
      };

    const onSelect = (selectedKeys, info) => {
        console.log('selected', selectedKeys, info);
        
      };
    
    const onCheck = (checkedKeys, info) => {
        setChecked(checkedKeys)
        console.log('onCheck', checkedKeys, info);
    };

    
    const  handleCancel = () => {
        setVisible(false)
    };
    
    const showModalAdd = () => {
        setOnEdit(false)
        console.log({onEdit})
        setVisible(true)
    };

    const ModalAdd = () =>{
        return <>
            <Modal
                visible={visible}
                title="Add new category"
                onOk={createCategory}
                onCancel={handleCancel}
                footer={[
                    <Button key="back" onClick={handleCancel}>
                    Close
                    </Button>,
                    <Button key="submit" type="primary" loading={loading} onClick={createCategory}>
                    Create
                    </Button>
                ]}
                >
                <Input style={{marginBottom :'20px'}} value={categoryName} placeholder="Category Name" onChange={(e)=> setCategoryName(e.target.value)} />
                <select className="group-control-model" value={parentCategoryId} onChange={(e)=> setParentCategoryId(e.target.value)} placeholder="Please select a category">
                    <option>Select a category</option>
                    {
                        createCategoryList(categories).map(option =><option key={option.value} value={option.value}>{option.name}</option>)
                    }
                </select>
            </Modal> 
        </>
      }

    const showModalEdit= () => {
        setOnEdit(true)
        console.log({onEdit})
        setVisible(true)
    };

    const ModalEdit = () =>{
        return <>
            <Modal
                visible={visible}
                title="Edit new category"
                onOk={createCategory}
                onCancel={handleCancel}
                width={1000}
                footer={[
                    <Button key="back" onClick={handleCancel}>
                    Close
                    </Button>,
                    <Button key="submit" type="primary" loading={loading} onClick={editCategory}>
                    Edit
                    </Button>
                ]}
                >
                    <Row>
                        <Col>
                            <h6>Expanded</h6>
                        </Col>
                    </Row>

                    <Row gutter={[8, 8]}>
                        <Col xs={{span: 24}} sm={{span: 24}} md={{span: 8}} lg={{span: 8}} lx={{span: 8}}>
                            <Input style={{marginBottom :'20px'}} value={categoryName} placeholder="Category Name" onChange={(e)=> setCategoryName(e.target.value)} />
                        </Col>

                        <Col xs={{span: 24}} sm={{span: 24}} md={{span: 8}} lg={{span: 8}} lx={{span: 8}}>
                        <select className="group-control-model"  value={parentCategoryId} onChange={(e)=> setParentCategoryId(e.target.value)} placeholder="Please select a category">
                            <option>Select a category</option>
                            {
                                createCategoryList(categories).map(option =><option key={option.value} value={option.value}>{option.name}</option>)
                            }
                        </select>
                        </Col>

                        <Col  xs={{span: 24}} sm={{span: 24}} md={{span: 8}} lg={{span: 8}} lx={{span: 8}}>
                        <select className="group-control-model">
                                <option value="">Select Type</option>
                                <option value="store">Store</option>
                                <option value="product">Product</option>
                                <option value="page">Page</option>
                        </select>
                        </Col>
                    </Row>

                    <Row gutter={[8, 8]}>
                        <Col xs={{span: 24}} sm={{span: 24}} md={{span: 8}} lg={{span: 8}} lx={{span: 8}}>
                            <Input style={{marginBottom :'20px'}} value={categoryName} placeholder="Category Name" onChange={(e)=> setCategoryName(e.target.value)} />
                        </Col>

                        <Col xs={{span: 24}} sm={{span: 24}} md={{span: 8}} lg={{span: 8}} lx={{span: 8}}>
                        <select className="group-control-model"  value={parentCategoryId} onChange={(e)=> setParentCategoryId(e.target.value)} placeholder="Please select a category">
                            <option>Select a category</option>
                            {
                                createCategoryList(categories).map(option =><option key={option.value} value={option.value}>{option.name}</option>)
                            }
                        </select>
                        </Col>

                        <Col  xs={{span: 24}} sm={{span: 24}} md={{span: 8}} lg={{span: 8}} lx={{span: 8}}>
                        <select className="group-control-model">
                                <option value="">Select Type</option>
                                <option value="store">Store</option>
                                <option value="product">Product</option>
                                <option value="page">Page</option>
                        </select>
                        </Col>
                    </Row>
            </Modal> 
        </>
      }

    const renderCategories = (cate) => {
        console.log(cate);
        let myCategories = [];
        cate.map((cat) => {
            myCategories.push({
            title: cat.name,
            key: cat._id,
            children:cat.children.length > 0 && renderCategories(cat.children),
          });
        });
        return myCategories;
    }; 

    
    const createCategoryList = (cate, options =[]) => {

        cate.map((cat) =>{
            options.push({value:cat._id, name: cat.name, parentId: cat.parentId});
            if(cat.children.length > 0)
            {
                createCategoryList(cat.children, options)
            }
        })

        return options;
    };

    const createCategory = async e => {
        const form = new FormData();
        form.append('name', categoryName)
