import { useState, useEffect } from 'react';
import Helmet from 'react-helmet';
import { Row, Col } from 'antd';
import './css/form.css'
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import axios from 'axios'
import {useDispatch,useSelector} from 'react-redux'
import Loading from '../utils/loading/Loading'

const initialState = {
    title: '',
    body: '',
    description: '',
    _id: ''
}

function CreatePost() {
    const token = useSelector(state => state.token)
    const auth = useSelector(state => state.auth)
    const {user, isAdmin} = auth
    const [myPost, setMyPost] = useState(initialState)
    const [slug, setSlug] = useState('');
    const [slugButton, setSlugButton] = useState(false);

    const handleChangeInput = e =>{
        const {name, value} = e.target
        setMyPost({...myPost, [name]:value})

        const createSlug = e.target.value.trim().split(' ').join('-');
		setSlug(createSlug);
    }

    const slugHandle = (e) => {
        setSlugButton(true);
		setSlug(e.target.value);
	};

    const handleURL = (e) => {
		e.preventDefault();
		setSlug(slug.trim().split(' ').join('-'));
	};
    return (
<div className="create">
        <Helmet>
			<title>Create new post</title>
			<meta name='description' content='Create a new post' />
		</Helmet>

        <div className="create-product">
        <h2>Create a new post</h2>
                    <form>
                        <Row gutter={[48, 16]}>
                            <Col xs={24} sm={24} md={24} lg={24} xl={12}>
                               <div className ='card'>
                                    <div className="group">
                                        <label htmlFor="title">Post Title</label>
                                        <input className="group-control" type="text" name="title" id="title" placeholder='Post title...' onChange={handleChangeInput}/>
                                    </div>

                                    <div className="group">
                                        <label className='image__label' htmlFor="image"></label>
                                        <input type="file" name="file" id="file"/>
                                    </div>
                                    
                                    <div className='group'>
                                                <label htmlFor='body'>Post body</label>
                                                <ReactQuill
                                                    theme='snow'
                                                    id='body'
                                                    placeholder='Post body...'
                                                />
                                    </div>
                                    
                                    <div className='group'>
									<label htmlFor='description'>Meta Description</label>
									<textarea
										name='description'
										id='description'
										cols='30'
										rows='10'
										className='group-control'
										placeholder='meta description...'
										maxLength='150'></textarea>
									<p className='length'>
                                        
									</p>
								</div>
                               </div>
                            </Col>

                            <Col xs={24} sm={24} md={24} lg={24} xl={12}>
                                <div className="card">
                                    <div className='group'>
                                        <label htmlFor='slug'>Post URL</label>
                                        <input
                                            type='text'
                                            name='slug'
                                            id='slug'
                                            className='group-control'
                                            placeholder='Post URL...'
                                            onChange={slugHandle}
                                            value={slug}
                                        />
                                    </div>

                                    <div className='group'>
                                        {slugButton ? (
                                            <button class='btn btn-default' onClick={handleURL}>
                                                Update Slug
                                            </button>
                                        ) : (
                                            ''
                                        )}
                                    </div>

                                    <div className='group'>
                                        <div className='imagePreview'>
                                        </div>
                                    </div>

                                    <div className='group'>
                                                <input
                                                    type='submit'
                                                    value='CREATE POST'
                                                    className='btn'
                                                />
                                    </div>
                                </div>
                            </Col>
                        </Row>
                    </form>

        </div>
    </div>
    );
}

export default CreatePost;
