import React, {useContext, useState, useEffect} from 'react'
import {useParams, Link} from 'react-router-dom'
import {GlobalState} from '../../../GlobalState'
import { Row, Col } from 'antd';

function DetailPost() {
    const params = useParams()
    const state = useContext(GlobalState)
    const [posts, setPosts] = state.myPostsAPI.posts
    const [title, setTitle] = state.myPostsAPI.title
    const [detailPost, setDetailPost] = useState([])
    console.log({params})

    useEffect(() =>{
        if(params.id){

            posts.forEach(post => {
                if(post._id === params.id){
                    localStorage.setItem('id_post', post._id)
                    setDetailPost(post)
                    setTitle(post.title)
                } 
            })
        }
    },[params.id, posts,setTitle])

    if(detailPost.length === 0) return null;
    return (
        <>
        <div class="bg-gray-100 lg:py-28 h-full">
            <div className='container'>
                <Row gutter={[40, 40]} >
                    <Col xs={{span: 24}} sm={{span: 24}} md={{span: 24}} lg={{span: 18}} lx={{span: 18}}>
                        <h1 className='mt-20 text-4xl tracking-wide'>{detailPost.title}</h1>
                    </Col>
                </Row>
            </div>
        </div>
        </>
    );
}

export default DetailPost;
