import React, {useContext, useState} from 'react'
import {GlobalState} from '../../../GlobalState'
import {useSelector} from 'react-redux'
import axios from 'axios'
import {Link} from 'react-router-dom'
import { Avatar} from 'antd';
import './css/alluser.css'

function AllProduct() {
    const state = useContext(GlobalState)
    const [products, setProducts] = state.productsAPI.products
    const auth = useSelector(state => state.auth)
    const {isAdmin} = auth
    const token = useSelector(state => state.token)
    const [callback, setCallback] = state.productsAPI.callback
    const [loading, setLoading] = useState(false)
    const [isCheck, setIsCheck] = useState(false)
    console.log(state)


    const deleteProduct = async(id, public_id) => {
        try {
            setLoading(true)
            const destroyImg = axios.post('/api/destroy_admin', {public_id},{
                headers: {Authorization: token}
            })
            const deleteProduct = axios.delete(`/admin/api/products/${id}`, {
                headers: {Authorization: token}
            })

            await destroyImg
            await deleteProduct
            setCallback(!callback)
            setLoading(false)
        } catch (err) {
            alert(err.response.data.msg)
        }
    }
    return (
<>
        <div className="col-right">
                    <h2>All Products</h2>

                    <div style={{overflowX: "auto"}}>
                        <table className="customers">
                            <thead>
                                <tr>
                                    <th>ID</th>
                                    <th>Name</th>
                                    <th>Price</th>
                                    <th>Description</th>
                                    <th>Content</th>
                                    <th>Stock</th>
                                    <th>Image</th>
                                    <th>Acttion</th>
                                </tr>
                            </thead>
                            <tbody>
                            {
                                    products.map(product => (
                                        <tr key={product._id}>
                                            <td>{product._id}</td>
                                            <td>{product.name}</td>
                                            <td>{product.price}</td>
                                            <td>{product.description}</td>
                                            <td>{product.content}</td>
                                            <td>{product.stock}</td>
                                            <td><Avatar shape="square" src={product.images} /></td>
                                            <td>
                                                <Link to={`/admin/edit_user/${product._id}`}>
                                                    <i className="fas fa-edit" title="Edit"></i>
                                                </Link>
                                                <i className="fas fa-trash-alt" title="Remove"
                                                onClick={() => deleteProduct(product._id,product.images.public_id)} ></i>
                                            </td>
                                        </tr>
                                    ))
                                }
                            </tbody>
                        </table>
                    </div>
                </div>
        </>
    );
}

export default AllProduct;
