import React,{useContext,useState,useSelector} from 'react';
import { Input, Space } from 'antd';
import {GlobalState} from '../../../GlobalState'
import moment from 'moment';
import { Modal, Button } from 'antd';
import {Link} from 'react-router-dom'
import axios from 'axios'

const { Search,TextArea } = Input;

function Review() {
    const state = useContext(GlobalState)
    const [productId, setProductId] = state.productsAPI.productId
    const [reviewsProduct] = state.productsAPI.reviewsProduct
    const [reply, setReply] = useState('')
    const [onAddReply, setOnAddReply] = useState(false)
    const [onEditReply, setOnEditReply] = useState(false)
    const [visible, setVisible] = useState(false)
    const token = useSelector(state => state.token)
    const onSearch= e => {
        console.log(e)
        setProductId(e)
    };

    const showModalAddReply = () => {
        setVisible(true)
        setOnAddReply(true)  
    };

    const showModalEditReply = () => {
        setVisible(true)
        setOnEditReply(true)  
    };

    const  handleCancel = () => {
        setOnAddReply(false) 
        setOnEditReply(false)
        setVisible(false)
    };

    const ModalAdd = () =>{
        return <>
            <Modal
                visible={visible}
                title="Enter reply"
                //onOk={createCategory}
                onCancel={handleCancel}
                footer={[
                    <Button key="back" onClick={handleCancel}>
                    Close
                    </Button>,
                    <Button key="submit" type="primary">
                        {
                            onAddReply && "Reply"|| onEditReply && "Edit reply"
                        }
                    </Button>
                ]}
                >
                <TextArea style={{marginBottom :'20px'}}  placeholder="Enter reply" onChange={handleReply}/>
            </Modal> 
        </>
      }

      const handleReply = async e => {
        console.log(e);
        setReply(e)
      };

      const createReply = async e =>{
        e.preventDefault()
        try {
            if(onAddReply){
                const res = await axios.put('/api/review_reply', {productId:productId, }, {
                    headers: {Authorization: token}
                })
            }
            
        } catch (err) {
            alert(err.response.data.msg)
        }
    }
    return (
        <div className="col-right">
             <h2>Reviews</h2>
             <Search placeholder="Enter ID Product" onSearch={onSearch} enterButton />
             
             <div style={{overflowX: "auto"}} className="mt-5">
                 {
                     reviewsProduct && reviewsProduct.length > 0 && 
                     <table className="customers">
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Comment</th>
                                <th>Rating</th>
                                <th>User</th>
                                <th>Time</th>
                                <th>Reply</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                reviewsProduct.map(r =>(
                                    <tr key={r._id}>
                                        <td>{r._id}</td>
                                        <td>{r.comment}</td>
                                        <td>{r.rating}</td>
                                        <td>{r.name}</td>
                                        <td>{moment(r.time).fromNow()}</td>
                                        <td>
                                            <Link onClick={showModalAddReply} className="mr-2">Add reply</Link>
                                            <Link className="mr-2">|</Link>
                                            <Link onClick={showModalEditReply}>Edit reply</Link>
                                            {
                                                ModalAdd()
                                            }
                                        </td>
                                    </tr>
                                ))
                            }
                        </tbody>
                    </table>||<p>Do not reviews</p>
                 }
            </div>
        </div>
    );
}

export default Review;
