import React, {useContext, useState, useEffect} from 'react'
import {useParams, Link} from 'react-router-dom'
import {GlobalState} from '../../../GlobalState'
import { Row, Col } from 'antd';
import { createFromIconfontCN } from '@ant-design/icons';
import Foot from './pawprint.svg'
import moment from 'moment';

function DetailPost() {
    const params = useParams()
    const state = useContext(GlobalState)
    const [posts, setPosts] = state.myPostsAPI.posts
    const [title, setTitle] = state.myPostsAPI.title
    const [detailPost, setDetailPost] = useState([])

    
    const IconFont = createFromIconfontCN({
        scriptUrl: [
            '//at.alicdn.com/t/font_2520839_d0ukkwu58tb.js'
        ],
      });
    console.log({params})

    useEffect(() =>{
        if(params.id){

            posts.forEach(post => {
                if(post._id === params.id){
                    localStorage.setItem('id_post', post._id)
                    setDetailPost(post)
                    setTitle(post.title)
                } 
            })
        }
    },[params.id, posts,setTitle])

    console.log({detailPost})

    if(detailPost.length === 0) return null;

    
    return (
        <>
                        <div dangerouslySetInnerHTML={detailPost.body}/>
        </>
    );
}

export default DetailPost;
