import React, {useEffect,useContext} from 'react';
import {useSelector} from 'react-redux'
import {GlobalState} from '../../../../GlobalState';
import {Link} from 'react-router-dom';
import './headerad.css'
import { Layout, Row, Col, Icon, Badge, Menu, Dropdown, Avatar, Popover} from 'antd';
import { createFromIconfontCN } from '@ant-design/icons';
import axios from 'axios'

const { Header} = Layout;

function HeaderAdmin() {
    const state = useContext(GlobalState)
    console.log(state)
    const auth = useSelector(state => state.auth)
    const {user, isLogged} = auth
    
const handleLogout = async () => {
    try {
        await axios.get('/user/logout')
        localStorage.removeItem('firstLogin')
        window.location.href = "/";
    } catch (err) {
        window.location.href = "/";
    }
}

const IconFont = createFromIconfontCN({
    scriptUrl: '//at.alicdn.com/t/font_2520839_uh3tkrpy8vb.js',
});


const menu =(
    <Menu>
      <Menu.Item icon={<IconFont type="iconlogout" style={{ fontSize: '20px'}}/>}>
        <Link to='/' onClick={handleLogout}>
          Sign Out
        </Link>
      </Menu.Item>
    </Menu>
  );

    return (
        <div className='header-admin'>
<Header style={{ background: '#fff', padding: 0 }}>
        <Row type="flex" justify="end" align="middle">
          <Col span={3}>
            <Badge className="header-icon" count={5}>
              <Link to="/mailbox">
                <Icon type="mail" />
              </Link>
            </Badge>
            <Popover  title="Title" trigger="click">
              <Badge className="header-icon" dot>
                <a href="#">
                  <Icon type="notification" />
                </a>
              </Badge>
            </Popover>
          </Col>
          <Col span={3}>
            <Dropdown overlay={menu}>
              <a className="ant-dropdown-link" href="#">
                <Avatar style={{ verticalAlign: 'middle'}}></Avatar> <Icon type="down" />
              </a>
            </Dropdown>
          </Col>
        </Row>
      </Header>
        </div>
    );
}

export default HeaderAdmin;
