import React, {useState, useEffect, useContext} from 'react'
import {useParams} from 'react-router-dom'
import {GlobalState} from '../../../GlobalState'
import { Table, Tag, Space } from 'antd';

function OrderDetails() {
    const state = useContext(GlobalState)
    const [history] = state.userAPI.history
    const [orderDetails, setOrderDetails] = useState([])

    const params = useParams()

    useEffect(() => {
        if(params.id){
            history.forEach(item =>{
                if(item._id === params.id) {
                    localStorage.setItem('id_payment', item._id)
                    setOrderDetails(item)
                    console.log({item})
                }
            })
        }
    },[params.id, history])

    const column1 = [
        {
          title: 'Name',
          dataIndex: 'recipient_name',
          key: 'recipient_name'
        },
        {
          title: 'Address',
          dataIndex: 'line1',
          key: 'line1',
        },
        {
          title: 'Postal Code',
          dataIndex: 'postal_code',
          key: 'postal_code'
        },
        {
          title: 'Country Code',
          dataIndex: 'country_code',
          key: 'country_code'
        }
      ];

       const rederData1 = (data) =>{
        let myData =[];
            myData.push({
            key: data._id,
            recipient_name:data.address.recipient_name,
            line1:data.address.line1 + " - " + data.address.city,
            postal_code:data.address.postal_code,
            country_code:data.address.country_code
        });
      return myData;
    }
 

    if(orderDetails.length === 0) return null;
    return (
        <div className="container mt-20">
            <Table columns={column1} dataSource={rederData1(orderDetails)}/>
        </div>
    );
}

export default OrderDetails;
