import {useState, useEffect} from 'react'
import axios from 'axios'

function MyPostsAPI(token) {
    const [myPosts, setMyPosts] = useState([])
    const [callback, setCallback] = useState(false)

    
    console.log("test post token:")
    console.log(token)
    useEffect(() =>{
        if(token){
            const getMyPosts = async () =>{
                try {
                    const res = await axios.get('/api/admin/my_posts', {
                        headers: {Authorization: token}
                    })
                    console.log("test token post")
                    console.log(token)

                    console.log('res post')
                    console.log(res)
                    setMyPosts(res.data.myPosts)
                } catch (err) {
                    alert(err.response.data.msg)
                }
            }

            getMyPosts()
            
        }
    },[token,callback])

    return {
        myPosts: [myPosts, setMyPosts],
        callback: [callback, setCallback]
    }

}

export default MyPostsAPI;
