import React, {useContext, useEffect} from 'react'
import {GlobalState} from '../../../GlobalState'
import {Link} from 'react-router-dom'
import axios from 'axios'
import {useSelector} from 'react-redux'
import { Tabs, Button } from 'antd';

const { TabPane } = Tabs;

function OrderHistory() {
  const state = useContext(GlobalState)
  const [history, setHistory] = state.userAPI.history

  const rederHistoryTab1 = (his) =>{
    let myHistory =[];
    his.map((h) => {
      if(h.status === 1)
      {
        myHistory.push(
          <p>{h.phone}</p>
        );
      }

  });
  return myHistory;
}

const panes = [
  { title: 'Tất cả', content: 'Tất cả', key: '1' },
  { title: 'Chưa thanh toán', content: rederHistoryTab1(history), key: '2' },
  { title: 'Đã thanh toán', content: 'Đã thanh toán', key: '3' },
  { title: 'Đã hủy', content: 'Đã thanh toán', key: '4' }
];
  return (
    <div className="container mt-20">
        <Tabs>
          {panes.map(_ =>(
            <TabPane tab={_.title} key={_.key}>
            {_.content}
            </TabPane>
          ))}
        </Tabs>
    </div>
  );
}

export default OrderHistory;
