import React, {useEffect,useContext} from 'react';
import {useSelector} from 'react-redux'
import {GlobalState} from '../../../../GlobalState';
import {Link} from 'react-router-dom';
import './headerad.css'
import { Layout, Row, Col,  Menu, Dropdown, Avatar} from 'antd';
import { createFromIconfontCN } from '@ant-design/icons';
import axios from 'axios'

const { Header} = Layout;

function HeaderAdmin() {
    const state = useContext(GlobalState)
    console.log(state)
    const auth = useSelector(state => state.auth)
    const {user, isLogged} = auth
    
const handleLogout = async () => {
    try {
        await axios.get('/user/logout')
        localStorage.removeItem('firstLogin')
        window.location.href = "/";
    } catch (err) {
        window.location.href = "/";
    }
}

const IconFont = createFromIconfontCN({
    scriptUrl: '//at.alicdn.com/t/font_2520839_uh3tkrpy8vb.js//at.alicdn.com/t/font_2520839_oxs5w5hu72h.js',
});


const menu =(
    <Menu>
      <Menu.Item icon={<IconFont type="iconlogout" style={{ fontSize: '20px'}}/>}>
        <Link to='/' onClick={handleLogout}>
          Sign Out
        </Link>
      </Menu.Item>
    </Menu>
  );

    return (
        <div className='header-admin'>
            <Header style={{ background: '#001529', padding: 0 }}>
                <Row type="flex" justify="end" align="middle">
                <Col span={3}>
                    <Link>
                        <Dropdown overlay={menu} placement="bottomCenter">
                            <div>
                                <Avatar size={42} src={user.avatar}/>
                                <span style={{margin:12, color:'white'}}>
                                    {user.name}
                                    <IconFont type="icondown" style={{ fontSize: '20px'}}/>
                                </span>
                            </div>
                        </Dropdown>
                    </Link>
                </Col>
                </Row>
            </Header>
        </div>
    );
}

export default HeaderAdmin;
