/* 
import React, {useContext, useEffect} from 'react'
import {GlobalState} from '../../../GlobalState'
import {Link} from 'react-router-dom'
import axios from 'axios'
import {useSelector} from 'react-redux'
import "aos/dist/aos.css"
import { Table, Tag, Space } from 'antd';

function OrderHistory() {
    const state = useContext(GlobalState)
    const [history, setHistory] = state.userAPI.history
    const [historyFalse, setHistoryFalse] = state.userAPI.historyFalse
    const [historyTrue, setHistoryTrue] = state.userAPI.historyTrue


    const columns1 = [
        {
          title: 'Payment ID',
          dataIndex: 'paymentID',
          key: 'paymentID'
        },
        {
          title: 'Date of Purchased',
          dataIndex: 'createdAt',
          key: 'createdAt',
        },
        {
          title: 'Action',
          dataIndex: 'action',
          key: 'action'
        },
      ];

      const rederHistory1 = (his) =>{
          let myHistory =[];
          his.map((h) => {
            myHistory.push({
            key: h._id,
            paymentID: h.paymentID,
            createdAt: new Date(h.createdAt).toLocaleDateString(),
            action:
                <Space size="middle">
                    <Link to={`/history/${h._id}`}>View</Link>
                </Space>
          });
        });
        return myHistory;
      }
      
    return (
        <div className="container mt-20">
            <Table columns={columns1} dataSource={rederHistory1(historyTrue)} scroll={{ x: 1300 }}/>
        </div>
    );
}

export default OrderHistory; */
