import React, {useState, useContext} from 'react'
import {GlobalState} from '../../../GlobalState'
import axios from 'axios'
import {useSelector} from 'react-redux'
import './css/alluser.css'
import './css/category.css'
import {Link} from 'react-router-dom'
import { Row, Col } from 'antd';

function Categories() {
    const state = useContext(GlobalState)
    console.log(state)
    const [categories] = state.categoriesAPI.categories
    const [category, setCategory] = useState('')

    const token = useSelector(state => state.token)
    const [callback, setCallback] = state.categoriesAPI.callback
    const [onEdit, setOnEdit] = useState(false)
    const [id, setID] = useState('')

    const createCategory = async e =>{
        e.preventDefault()
        try {
            if(onEdit){
                const res = await axios.put(`/api/category/${id}`, {name: category}, {
                    headers: {Authorization: token}
                })
                alert(res.data.msg)
            }else{
                const res = await axios.post('/api/category', {name: category}, {
                    headers: {Authorization: token}
                })
                alert(res.data.msg)
            }
/*             setOnEdit(false)
            setCategory('') */
            setCallback(!callback)
            
        } catch (err) {
            alert(err.response.data.msg)
        }
    }
    return (
        <>
        <div className="col-right">
                    <h2>All Categories</h2>
                    
                    <form className='category' onSubmit={createCategory}>
                    <Row gutter={[8, 8]} type="flex" justify="end" align="center">
                        <Col span={10}><input type="text" name="category" placeholder="Category's name" value={category} required onChange={e => setCategory(e.target.value)} /></Col>
                        <Col span={2}><button type="submit">Create</button></Col>
                    </Row>
                    </form>

                    <div style={{overflowX: "auto"}}>
                        <table className="customers">
                            <thead>
                                <tr>
                                    <th>ID</th>
                                    <th>Name</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    categories.map(category =>(
                                        <tr key={category._id}>
                                            <td>{category.name}</td>
                                            <td>
                                                <Link>
                                                    <i className="fas fa-edit" title="Edit"></i>
                                                </Link>
                                                <i className="fas fa-trash-alt" title="Remove"></i>
                                            </td>
                                        </tr>
                                    ))
                                }
                            </tbody>
                        </table>
                    </div>
                </div>
        </>
    );
}

export default Categories;
