import {useState, useEffect,useContext} from 'react'
import axios from 'axios'
import {GlobalState} from '../../../../GlobalState'
import { Row, Col } from 'antd';
import { Select } from 'antd';

const { Option } = Select;

function SearchPet() {
    const state = useContext(GlobalState)
    const [type, setType] = useState(false)
    const [status, setStatus] = useState(false)
    const [searchName, setSearchName] = useState(false)
    const [statuses] = state.statusesAPI.statuses
    const [types] = state.typesAPI.types
    const [callback, setCallback] = state.petsAPI.callback

    const handleType = e => {
        console.log('e.target.value')
        console.log(e.target.value)
        setType(e.target.value)
    }

    const handleSubmit = async e => {
        e.preventDefault()

        try {
            console.log(type)
            if(type)
            {
                const res = await axios.get(`/api/pets?${type}`)
            }
        } catch (err) {
            
        }
    }

    return (
        <form className='mr-b' onSubmit={handleSubmit}>
            <Row gutter={[48, 48]}>
                <Col xs={24} sm={24} md={24} lg={12} xl={8}>
                <select class="group-search" name="type" value={type} onChange={handleType} >
                    <option value="">All Type</option>
                        {
                            types.map(type => (
                                <option value={"type=" + type.name} key={type._id}>
                                    {type.name}
                                </option>
                        ))
                        }
                </select>
                </Col>

                <Col xs={24} sm={24} md={24} lg={12} xl={8}>

                </Col>
                <Col xs={24} sm={24} md={24} lg={12} xl={8}>
                    
                </Col>
            </Row>

            <button type="submit">Tìm Kiếm</button>
        </form>
    );
}

export default SearchPet;
