import ACTIONS from './index'
import axios from 'axios'



export const createAction = async (postData,token) => {
	return async (dispatch) => {
        try {
            const config = {
                headers: {Authorization: token}
			};
			const {
				data: { msg },
			} = await axios.post('/api/admin/posts/new', postData, config);
        } catch (err) {
            
        }
	};
};
