import React, {useState, useContext} from 'react'
import {GlobalState} from '../../../GlobalState'
import axios from 'axios'
import {useSelector} from 'react-redux'
import './css/alluser.css'
import './css/category.css'
import {Link} from 'react-router-dom'
import { Row, Col } from 'antd';
import {showSuccessMsg, showErrMsg} from '../utils/notification/Notification'
import { Modal, Button } from 'antd';
import { Input } from 'antd';




function Categories() {
    const state = useContext(GlobalState)
    console.log(state)
    const [categories] = state.categoriesAPI.categories
    const [category, setCategory] = useState('')

    const token = useSelector(state => state.token)
    const [callback, setCallback] = state.categoriesAPI.callback
    const [onEdit, setOnEdit] = useState(false)
    const [id, setID] = useState('')

    const [err, setErr] = useState(false)
    const [success, setSuccess] = useState(false)

    const [visible, setVisible] = useState(false)
    const [loading, setLoading] = useState(false)

    const [categoryName, setCategoryName] = useState('')
    const [parentCategoryId, setParentCategoryId] = useState('')

    

    

    console.log({categories})

    const createCategory = async e =>{
        e.preventDefault()
        try {
            if(onEdit){
                const res = await axios.put(`/api/category/${id}`, {name: category}, {
                    headers: {Authorization: token}
                })
                setSuccess('Update is successful')
            }else{
                const res = await axios.post('/api/category', {name: category}, {
                    headers: {Authorization: token}
                })
                setSuccess('Create is successful')
            }
            setOnEdit(false)
            setCategory('') 
            setCallback(!callback)
            
        } catch (err) {
            alert(err.response.data.msg)
        }

        return <>
        {success && showSuccessMsg('success',success)}
        {err && showErrMsg('error',err)}
        </>
    }


    const editCategory = async (id, name) =>{
        setID(id)
        setCategory(name)
        setOnEdit(true)
        setSuccess('Update is successful')

        return <>
        {success && showSuccessMsg('success',success)}
        {err && showErrMsg('error',err)}
        </>
    }

    const deleteCategory = async id =>{
        try {
            const res = await axios.delete(`/api/category/${id}`, {
                headers: {Authorization: token}
            })
            setSuccess('Delete is successful')
            setCallback(!callback)
        } catch (err) {
            setErr(err.response.data.msg)
        }
        return <>
        {success && showSuccessMsg('success',success)}
        {err && showErrMsg('error',err)}
        </>
    }


     const renderCategories = (cate) => {
        let myCategories = [];
        for (let cat of cate) {
          myCategories.push(
              <li key={cat.name}>
                  {cat.name}
                  {cat.children.length > 0 && <ul>{renderCategories(cat.children)}</ul> || ""}
                  
            </li>
          );
        }
        return myCategories;
    }; 


    
    const createCategoryList = (cate, options =[]) => {

        for (let cat of cate){
            options.push({value:cat._id, name: cat.name})
            if(cat.children.length > 0)
            {
                createCategoryList(cat.children, options)
            }
        }

        return options;
    }

    const showModal = () => {
        setVisible(true)
    };

    const handleOk = async e => {
        const form = new FormData();
        form.append('name', categoryName)
        form.append('parentId', parentCategoryId)
        const res = await axios.post('/api/category', form, {
            headers: {Authorization: token}
        })

        setCallback(!callback)
        console.log(res)
        setVisible(false)
      };

    const  handleCancel = () => {
            setVisible(false)
      };
    return (
        <>
        <Row justify="space-between">
            <Col xs={{span: 12}} sm={{span: 12}} md={{span: 12}} lg={{span: 12}} lx={{span: 12}}>
                <h2 className="text-4xl">All Categories</h2>
            </Col>
            <Col xs={{span: 12}} sm={{span: 12}} md={{span: 12}} lg={{span: 12}} lx={{span: 12}} className="text-right">
            <Button type="primary" onClick={showModal}>
                ADD
            </Button>
            <Modal
                visible={visible}
                title="Add new category"
                onOk={handleOk}
                onCancel={handleCancel}
                footer={[
                    <Button key="back" onClick={handleCancel}>
                    Close
                    </Button>,
                    <Button key="submit" type="primary" loading={loading} onClick={handleOk}>
                    Create
                    </Button>
                ]}
                >
                <Input style={{marginBottom :'20px'}} value={categoryName} placeholder="Category Name" onChange={(e)=> setCategoryName(e.target.value)} />
                <select value={parentCategoryId} onChange={(e)=> setParentCategoryId(e.target.value)} placeholder="Please select a category">
                    <option>Select a category</option>
                    {
                        createCategoryList(categories).map(option =><option key={option.value} value={option.value}>{option.name}</option>)
                    }
                </select>
            </Modal>  
            </Col>
        </Row>
        
        <ul>
            {renderCategories(categories)}
        </ul>
        </>
    );
}

export default Categories;
