import React, {useContext, useState, useEffect} from 'react'
import {useParams, Link} from 'react-router-dom'
import {GlobalState} from '../../../GlobalState'
import { Row, Col } from 'antd';
import { createFromIconfontCN } from '@ant-design/icons';
import Foot from './pawprint.svg'
import moment from 'moment';

function DetailPost() {
    const params = useParams()
    const state = useContext(GlobalState)
    const [posts, setPosts] = state.myPostsAPI.posts
    const [title, setTitle] = state.myPostsAPI.title
    const [detailPost, setDetailPost] = useState([])
    const [topics] = state.topicsAPI.topics
    const [filter, setFilter] = state.myPostsAPI.filter
    const [all, setALL] = state.myPostsAPI.all
    const [numPostByTopic, setNumPostByTopic] = state.myPostsAPI.numPostByTopic

    
    const IconFont = createFromIconfontCN({
        scriptUrl: [
            '//at.alicdn.com/t/font_2520839_d0ukkwu58tb.js'
        ],
      });
    console.log({params})
    

    useEffect(() =>{
        if(params.id){

            posts.forEach(post => {
                if(post._id === params.id){
                    localStorage.setItem('id_post', post._id)
                    setDetailPost(post)
                    setTitle(post.title)
                } 
            })
        }
    },[params.id, posts,setTitle])

    console.log({detailPost})

    const handleFilter = async (text) => {
        setFilter(text)
        console.log({filter})
        console.log({text})
    }

    if(detailPost.length === 0) return null;

    
    return (
        <>
        <div class="bg-gray-100 lg:py-28 h-full">
            <div className='container'>
                <Row gutter={[40, 40]} >
                    <Col xs={{span: 24}} sm={{span: 24}} md={{span: 24}} lg={{span: 18}} lx={{span: 18}}>
                        <div className="border-b-2 border-white px-1 py-4">
                            <h1 className='mt-20 text-5xl tracking-wide leading-relaxed text-black'>{detailPost.title}</h1>
                            <div className="flex items-center">
                                <img src={Foot} className="w-14 h-14 rounded-full mr-4" alt=""></img>
                                <p className="text-gray-900 mb-0">{moment(detailPost.updatedAt).format('MM/DD/YYYY')} bởi <span className="text-blue-600">{detailPost.userName}</span></p>
                            </div>
                        </div>
                        <div className="text-black py-4" dangerouslySetInnerHTML={{ __html: detailPost.body }} />
                    </Col>

                    <Col xs={{span: 24}} sm={{span: 24}} md={{span: 24}} lg={{span: 6}} lx={{span: 6}}>
                        <h1 className='mt-20 text-5xl tracking-wide leading-relaxed'>Chuyên mục</h1>
                        <ul>
                            <Link onClick={() => handleFilter("")}>
                                        <li className="px-1 py-4 border-b border-t border-white hover:border-gray-200 transition duration-300">
                                            <Link className="flex items-center text-gray-600 cursor-pointer">
                                                <span className="inline-block h-4 w-4 bg-green-300 mr-3" />
                                                Tất cả bài viết
                                                <span className="text-gray-500 ml-auto">{all} bài viết</span>
                                                <i className="text-gray-500 bx bx-right-arrow-alt ml-1" />
                                            </Link>
                                        </li>
                            </Link>
                            
                            {
                                topics.map(topic => (
                                    <Link onClick={() => handleFilter("?topic="+topic._id)}  value={"topic=" + topic._id} key={topic._id}>
                                        <li className="px-1 py-4 border-b border-t border-white hover:border-gray-200 transition duration-300">
                                            <Link className="flex items-center text-gray-600 cursor-pointer">
                                                <span className="inline-block h-4 w-4 bg-green-300 mr-3" />
                                                {topic.name}
                                                <span className="text-gray-500 ml-auto" value={topic._id}>{numPostByTopic.find(_=>_._id === topic._id) && numPostByTopic.find(_=>_._id === topic._id).count || 0} bài viết</span>
                                                <i className="text-gray-500 bx bx-right-arrow-alt ml-1" />
                                            </Link>
                                        </li>
                                    </Link>
                                ))
                            }
                            </ul>
                    </Col>
                </Row>
            </div>
        </div>
        </>
    );
}

export default DetailPost;
