import React, {useContext, useState, useEffect} from 'react'
import {useParams, Link} from 'react-router-dom'
import {GlobalState} from '../../../GlobalState'
import { Row, Col} from 'antd';
import { Avatar } from 'antd';


function DetailPet() {
    const params = useParams()
    const state = useContext(GlobalState)
    const [pets, setPets] = state.petsAPI.pets
    const [detailPet, setDetailPet] = useState([])
    const [name_pet, setNamePet] = state.petsAPI.name_pet

    console.log('params',params)
    useEffect(() =>{
        if(params.id){

            pets.forEach(pet => {
                if(pet._id === params.id){
                    localStorage.setItem('id_pet', pet._id)
                    setDetailPet(pet)
                    setNamePet(pet.name)
                } 
            })
        }
    },[params.id, pets])

    console.log('detailPet',detailPet)

    if(detailPet.length === 0) return null;
    return (
        <>
        <div className='container'>
            <div className="lg:w-4/5 mx-auto flex flex-wrap">
                <img alt="ecommerce" className="lg:w-1/2 w-full object-cover object-center rounded border border-gray-200" src="https://www.whitmorerarebooks.com/pictures/medium/2465.jpg" />
                <div className="lg:w-1/2 w-full lg:pl-10 lg:py-6 mt-6 lg:mt-0">
                    <h1 className="text-gray-900 text-3xl title-font font-medium mb-1">The Catcher in the Rye</h1>
                </div>
            </div>
        </div>
        </>
    );
}

export default DetailPet;
