import ACTIONS from './index'
import axios from 'axios'



export const createAction = (postData,token) => {
	return async (dispatch) => {
        try {
            const config = {
                headers: {Authorization: token}
			};
			const {data} = await axios.post('/api/admin/posts/new', postData, config);

            console.log(data);
        } catch (err) {
            
        }
	};
};
