        setVisible(false)
      };

    const  handleCancel = () => {
            setVisible(false)
      };

    const renderCategories = (cat) => {
        let myCategories = [];
        for (let cate of cat) {
          myCategories.push(
              <li key={cate.name}>
                  {cate.name}
                  {cate.children.length > 0 && <ul>{renderCategories(cate.children)}</ul> || ""}
                  
