import React,{useContext,useState} from 'react';
import { Input, Space } from 'antd';
import {GlobalState} from '../../../GlobalState'
import moment from 'moment';
import { Modal, Button } from 'antd';

const { Search,TextArea } = Input;

function Review() {
    const state = useContext(GlobalState)
    const [productId, setProductId] = state.productsAPI.productId
    const [reviewsProduct] = state.productsAPI.reviewsProduct
    const [onReply, setOnReply] = useState(false)
    const [visible, setVisible] = useState(false)
    const onSearch= e => {
        console.log(e)
        setProductId(e)
    };

    const showModalReply = () => {
        setVisible(true)
        setOnReply(true)  
    };

    const  handleCancel = () => {
        setOnReply(false) 
        setVisible(false)
    };

    const ModalAdd = () =>{
        return <>
            <Modal
                visible={visible}
                title="Enter reply"
                //onOk={createCategory}
                onCancel={handleCancel}
                footer={[
                    <Button key="back" onClick={handleCancel}>
                    Close
                    </Button>,
                    <Button key="submit" type="primary">
                        Reply
                    </Button>
                ]}
                >
                <TextArea style={{marginBottom :'20px'}}  placeholder="Enter reply"/>
            </Modal> 
        </>
      }
    return (
        <div className="col-right">
             <h2>Reviews</h2>
             <Search placeholder="Enter ID Product" onSearch={onSearch} enterButton />
             
             <div style={{overflowX: "auto"}} className="mt-5">
                 {
                     reviewsProduct && reviewsProduct.length > 0 && 
                     <table className="customers">
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Comment</th>
                                <th>Rating</th>
                                <th>User</th>
                                <th>Time</th>
                                <th>Reply</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                reviewsProduct.map(r =>(
                                    <tr key={r._id}>
                                        <td>{r._id}</td>
                                        <td>{r.comment}</td>
                                        <td>{r.rating}</td>
                                        <td>{r.name}</td>
                                        <td>{moment(r.time).fromNow()}</td>
                                        <td>
                                            <i onClick={showModalReply} className="fas fa-edit" title="Reply"></i>
                                            {
                                                ModalAdd()
                                            }
                                        </td>
                                    </tr>
                                ))
                            }
                        </tbody>
                    </table>||<p>Do not reviews</p>
                 }
            </div>
        </div>
    );
}

export default Review;
