import React,{useContext} from 'react';
import { Row, Col } from 'antd';
import Health from './images/health.png'
import House from './images/house.png'
import {GlobalState} from '../../../GlobalState'

function Statistical() {
    const state = useContext(GlobalState)
    const [petsCount, setPetsCount] = state.petsAPI.petsCount
    const [numPetByStatus, setNumPetByStatus]  = state.petsAPI.numPetByStatus
    

    var arrImg = [Health, House];
    var arrStatus = [];
    arrStatus.push({count: petsCount, strStatus: "Ca cứu hộ",  objImg: Health});
    for (let i = 0; i < numPetByStatus.length; i++) {
        console.log(i);
        const e = numPetByStatus[i];
        arrStatus.push({count: e.pet_list.length, strStatus: e.name,  objImg: arrImg[0]});
    }
    // numPetByStatus.forEach(function(e, i){
    //     arrStatus.push({count: e.pet_list.length, strStatus: e.name,  objImg: arrImg[0]});
    // });

    // function chunkArray(myArray, chunk_size){
    //     var results = [];
    //     while (myArray.length) {
    //         results.push(myArray.splice(0, chunk_size));
    //     }
    //     return results;
    // }
    function chunkArray(myArray, chunk_size){
        var index = 0;
        var arrayLength = myArray.length;
        var tempArray = [];
        
        for (index = 0; index < arrayLength; index += chunk_size) {
            var myChunk = myArray.slice(index, index+chunk_size);
            // Do something if you want with the group
            tempArray.push(myChunk);
        }
        return tempArray;
    }




    // Split in group of 3 items
    var _arrStatus = chunkArray(arrStatus, 3);
    var arrStatus3 = _arrStatus.slice(0,_arrStatus.length-1);
    var StatusLast = _arrStatus[_arrStatus.length-1];
    console.log(arrStatus3);
    console.log(StatusLast);
  


    console.log(numPetByStatus.map((_)=>_.pet_list.length))
    return (
        <section className="static">
            <div className="container">
                <div className="number">
                    {
                        //n-1 hàng đầu
                        arrStatus3.map((rowStatus) => (
                            <Row gutter={[16, 16]}>
                                {
                                    rowStatus.map((status) => (
                                        <Col xs={{span: 24}} sm={{span: 24}} md={{span: 24}} lg={{span: 8}} lx={{span: 8}}>
                                            <div className="text-center items-center relative number-item">
                                                <div className="bg-white p-12 rounded-full inline-block mb-2"><img src={Health} alt =""/></div>
                                                <h1 className="mb-0 text-white text-8xl">{status.count}</h1>
                                                <p className="text-white text-3xl mt-2">{status.strStatus}</p>
                                            </div>
                                        </Col>
                                    ))//rowStatus.map(
                                }
                            </Row>
                        ))//arrStatus3.map(
                    }

                    <Row className="px-80 mt-3">
                        <Col xs={{span: 24}} sm={{span: 24}} md={{span: 24}} lg={{span: 12}} lx={{span: 12}}>
                            <div className="text-center items-center relative number-item">
                                <div className="bg-white p-12 rounded-full inline-block mb-2"><img src={Health} alt =""/></div>
                                <h1 className="mb-0 text-white text-8xl">100</h1>
                                <p className="text-white text-3xl mt-2">Ca cứu hộ</p>
                            </div>
                        </Col>

                        <Col xs={{span: 24}} sm={{span: 24}} md={{span: 24}} lg={{span: 12}} lx={{span: 12}}>
                            <div className="text-center items-center relative number-item">
                                <div className="bg-white p-12 rounded-full inline-block mb-2"><img src={Health} alt =""/></div>
                                <h1 className="mb-0 text-white text-8xl">100</h1>
                                <p className="text-white text-3xl mt-2">Ca cứu hộ</p>
                            </div>
                        </Col>
                    </Row>
                </div>
            </div>
        </section>
    );

    // return (
    //     <section className="static">
    //         <div className="container">
    //             <div className="number">
    //                 <Row gutter={[16, 16]}>
    //                     <Col xs={{span: 24}} sm={{span: 24}} md={{span: 24}} lg={{span: 8}} lx={{span: 8}}>
    //                         <div className="text-center items-center relative number-item">
    //                             <div className="bg-white p-12 rounded-full inline-block mb-2"><img src={Health} alt =""/></div>
    //                             <h1 className="mb-0 text-white text-8xl">{petsCount}</h1>
    //                             <p className="text-white text-3xl mt-2">Ca cứu hộ</p>
    //                         </div>
    //                     </Col>

    //                     <Col xs={{span: 24}} sm={{span: 24}} md={{span: 24}} lg={{span: 8}} lx={{span: 8}}>
    //                         <div className="text-center items-center relative number-item">
    //                             <div className="bg-white p-12 rounded-full inline-block mb-2"><img src={House} alt =""/></div>
    //                             <h1 className="mb-0 text-white text-8xl">{numPetByStatus[0].}</h1>
    //                             <p className="text-white text-3xl mt-2">Ca cứu hộ</p>
    //                         </div>
