import React from 'react'
import { Button, notification, Divider, Space } from 'antd';


export const showErrMsg = (placement,msg) => {
    notification.error({
        message: 'Error',
        description:`${msg}`,
        placement,
    });
}

export const showSuccessMsg = (msg) => {
    return <div className="successMsg">{msg}</div>
}
