import React,{useContext,useSelector} from 'react';
import BreadCrumb from '../BreadCumb'
import {GlobalState} from '../../../GlobalState'
import { Row, Col } from 'antd';
import PostItem from '../posts/Posts'
import { htmlToText } from 'html-to-text';
import { Link } from 'react-router-dom';
import moment from 'moment';
import Aos from 'aos'
import "aos/dist/aos.css"

function Posts() {
    const state = useContext(GlobalState)
    const [posts, setPosts] = state.myPostsAPI.posts

    console.log('state all post.js')
    console.log(posts)
    return (
        <>
        <div className='container'>
            <Row gutter={[16, 16]} style={{marginTop:'100px'}} >
                <Col xs={{span: 24}} sm={{span: 24}} lg={{span: 24}} lg={{span: 18}} lx={{span: 18}}>
                {
                    posts.map((post) => (
                        
                        <div className="max-w-lg mx-auto bg-white rounded-xl shadow-lg overflow-hidden lg:max-w-2xl">
                            <div className="lg:flex">
                                <div className="lg:flex-shrink-0">
                                <img className="h-48 w-full object-cover lg:h-full lg:w-48" src="/img/store.jpg" alt="Man looking at item at a store" />
                                </div>
                                <div className="p-8">
                                <div className="uppercase tracking-wide text-sm text-indigo-500 font-semibold">Case study</div>
                                <a href="#" className="block mt-1 text-lg leading-tight font-medium text-black hover:underline">Finding customers for your new business</a>
                                <p className="mt-2 text-gray-500">Getting a new business off the ground is a lot of hard work. Here are five ideas you can use to find your first customers.</p>
                                </div>
                            </div>
                        </div>
                       
                    ))
                }
                </Col>
                <Col xs={{span: 24}} sm={{span: 24}} lg={{span: 24}} lg={{span: 6}} lx={{span: 6}}>
                    
                </Col>
            </Row>
        </div>
        </>
    );
}

export default Posts;
