import axios from 'axios'

function CategoriesAPI() {
    const [categories, setCategories] = useState([])

    useEffect(() =>{
        const getCategories = async () =>{
            const res = await axios.get('/api/category')
            console.log(res)
            setCategories(res.data)
        }

        getCategories()
    },[])
    return (
        <div>
            
        </div>
    );
}

export default CategoriesAPI;
