import React, {useContext, useEffect} from 'react'
import {GlobalState} from '../../../GlobalState'
import {Link} from 'react-router-dom'
import axios from 'axios'
import {useSelector} from 'react-redux'
import { Tabs, Button } from 'antd';
import { Row, Col} from 'antd';
import { Avatar } from 'antd';
import "aos/dist/aos.css"
import Aos from 'aos'

const { TabPane } = Tabs;

function OrderHistory() {
  const state = useContext(GlobalState)
  const [history, setHistory] = state.userAPI.history

  useEffect(() => {
    Aos.init({duration: 2000});
  })

  const rederContentTab1 = (his) =>{
    let myHistory =[];
    his.map((h) => {

        myHistory.push(
          <p>{h.total}</p>
        );
  });
  return myHistory;
}

  const rederContentTab2 = (his) =>{
    let myHistory =[];
    his.map((h) => {
      if(h.status === 1)
      {
        myHistory.push(
          <Row gutter={[16, 16]}>
            <Col data-aos="zoom-out-left"  xs={{span: 24}} sm={{span: 24}} md={{span: 24}} lg={{span: 24}} lx={{span: 24}}>
              <div class="card-buy"></div>
            </Col>
          </Row>
        );
      }

  });
  return myHistory;
}

const panes = [
  { title: 'Tất cả', content: rederContentTab1(history), key: '1' },
  { title: 'Chưa thanh toán', content: rederContentTab2(history), key: '2' },
  { title: 'Đã thanh toán', content: 'Đã thanh toán', key: '3' },
  { title: 'Đã hủy', content: 'Đã thanh toán', key: '4' }
];
  return (
  <div className="bg-gray-100">
    <div className="container pt-20 pb-20">
      <div className="container-history-cart">
        <h2 className="text-4xl mb-10">Lịch sử mua hàng</h2>
        <Tabs>
          {panes.map(_ =>(
            <TabPane tab={_.title} key={_.key}>
            {_.content}
            </TabPane>
          ))}
        </Tabs>
      </div>
    </div>
  </div>
  );
}

export default OrderHistory;
