import React, {useContext, useState,useEffect} from 'react'
import axios from 'axios'
import {GlobalState} from '../../../GlobalState'
import { Avatar } from 'antd';
import {Link,useParams} from 'react-router-dom'
import './css/alluser.css'



function AllProduct() {
    const params = useParams()
    const state = useContext(GlobalState)
    const [products, setProducts] = state.productsAPI.products
    const [detailProduct, setDetailProduct] = useState([])

    useEffect(() =>{
        if(params.id){

            products.forEach(product => {
                if(product._id === params.id) setDetailProduct(product)
            })
        }
    },[params.id, products])

    if(detailProduct.length === 0) return null;
    console.log('state all products')
    console.log(products)

  

    return (
        <>
        <div className="col-right">
                    <h2>All Products</h2>

                    <div style={{overflowX: "auto"}}>
                        <table className="customers">
                            <thead>
                                <tr>
                                    <th>ID</th>
                                    <th>Name</th>
                                    <th>Price</th>
                                    <th>Description</th>
                                    <th>Content</th>
                                    <th>Stock</th>
                                    <th>Category</th>
                                    <th>Image</th>
                                    <th>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                            {
                                    products.map(product => {return product.category === detailProduct.category 
                                        ?
                                        <tr key={product._id}>
                                            <td>{product._id}</td>
                                            <td>{product.name}</td>
                                            <td>{product.price}</td>
                                            <td>{product.description}</td>
                                            <td>{product.content}</td>
                                            <td>{product.stock}</td>
                                            <td>{product.category}</td>
                                            <td> <Avatar shape="square" size={64} src={product.images.url}/></td>
                                            <td>
                                                <Link to={`/admin/edit_product/${product._id}`}>
                                                    <i className="fas fa-edit" title="Edit"></i>
                                                </Link>
                                                <i className="fas fa-trash-alt" title="Remove"></i>
                                            </td>
                                        </tr>:null
                                    })
                                }
                            </tbody>
                        </table>
                    </div>
                </div>
        </>
    );
}

export default AllProduct;
