/*     useEffect(() => {
        if(param.id){
            setOnEdit(true)
            products.forEach(product => {
                if(product._id === param.id) {
                    setProduct(product)
                    setImages(product.images)
                }
            })
        }else{
            setOnEdit(false)
            setProduct(initialState)
            setImages(false)
        }
    }, [param.id, products]) */

/*     const handleUpload = async e =>{
        e.preventDefault()
        try {
            if(!isAdmin) return alert("You're not an admin")
            const file = e.target.files[0]
            
            if(!file) return alert("File not exist.")

            if(file.size > 1024 * 1024) // 1mb
                return alert("Size too large!")

            if(file.type !== 'image/jpeg' && file.type !== 'image/png') // 1mb
                return alert("File format is incorrect.")

            let formData = new FormData()
            formData.append('file', file)

            setLoading(true)
            const res = await axios.post('/api/upload_admin', formData, {
                headers: {'content-type': 'multipart/form-data', Authorization: token}
            })
            setLoading(false)
            setImages(res.data)

        } catch (err) {
            alert(err.response.data.msg)
        }
    }

    const handleDestroy = async () => {
        try {
            if(!isAdmin) return alert("You're not an admin")
            setLoading(true)
            await axios.post('/api/destroy_admin', {public_id: images.public_id}, {
                headers: {Authorization: token}
            })
            setLoading(false)
            setImages(false)
        } catch (err) {
            alert(err.response.data.msg)
        }
    }

    const handleChangeInput = e =>{
        const {name, value} = e.target
        setProduct({...product, [name]:value})
    }

    const handleSubmit = async e =>{
        e.preventDefault()
        try {
            if(!isAdmin) return alert("You're not an admin")
            if(!images) return alert("No Image Upload")

            if(onEdit){
                await axios.put(`/api/products/${product._id}`, {...product, images}, {
                    headers: {Authorization: token}
                })
            }else{
                await axios.post('/api/products', {...product, images}, {
                    headers: {Authorization: token}
                })
            }
            setCallback(!callback)
            history.push("/")
        } catch (err) {
            alert(err.response.data.msg)
        }
    }

    const styleUpload = {
        display: images ? "block" : "none"
    } */
    return (
        <div className="create_product">
            <Row gutter={[32, 16]}>
                <Col span={12}>
                <div className="upload">
                    <input type="file" name="file" id="file_up"/>
                    <div id="file_img">
                        <img src="" alt=""/>
                        <span>X</span>
                    </div>
                </div>
                </Col>
                <Col span={12}></Col>
            </Row>


            <form>
                <div className="row">
                    <label htmlFor="product_id">Product ID</label>
                    <input class="form-control" type="text" name="product_id" id="product_id" required
                    />
                </div>

                <div className="row">
                    <label htmlFor="title">Title</label>
                    <input class="form-control" type="text" name="title" id="title" required
                     />
                </div>

                <div className="row">
                    <label htmlFor="price">Price</label>
                    <input class="form-control" type="number" name="price" id="price" required
                    />
                </div>

                <div className="row">
                    <label htmlFor="description">Description</label>
                    <textarea class="form-control" type="text" name="description" id="description" required
                    />
                </div>

                <div className="row">
                    <label htmlFor="content">Content</label>
                    <textarea class="form-control" type="text" name="content" id="content" required
                     />
                </div>

                <div className="row">
                    <label htmlFor="categories">Categories: </label>
                    <select class="form-control" name="category">
                        <option value="">Please select a category</option>
                        {
                            categories.map(category => (
                                <option value={category._id} key={category._id}>
                                    {category.name}
                                </option>
                            ))
                        }
                    </select>
                </div>

                <button class="btn btn-secondary" type="submit">Create</button>
            </form>
        </div>
    )
}

export default CreateProduct
