import React,{useContext,useSelector} from 'react';
import BreadCrumb from '../BreadCumb'
import {GlobalState} from '../../../GlobalState'
import { Row, Col } from 'antd';
import PostItem from '../posts/Posts'
import { htmlToText } from 'html-to-text';
import { Link } from 'react-router-dom';
import moment from 'moment';

function Posts() {
    const state = useContext(GlobalState)
    const [posts, setPosts] = state.myPostsAPI.posts

    console.log('state all post.js')
    console.log(posts)
    return (
        <>
        <div className='container'>
            <Row gutter={[16, 16]} style={{marginTop:'100px'}} >
                <Col data-aos="zoom-out-right" xs={{span: 24}} sm={{span: 24}} md={{span: 24}} lg={{span: 18}} lx={{span: 18}}>
                    {
                        posts.map((post) => (
                            
                                <div key={post._id} className="mb-10">
                                    <div className="h-48 bg-cover  text-center" style={{ backgroundImage: `url(${post.images.url})` }} title="Woman holding a mug"></div>
                                    <div className="border-r border-b border-l border-grey-light lg:border-l-0 lg:border-t lg:border-grey-light bg-white rounded-b lg:rounded-b-none lg:rounded-r p-4 flex flex-col justify-between leading-normal">
                                        <div className="mb-8">
                                            <div className="text-black font-bold text-5xl mb-2">{post.title}</div>
                                            <p className="text-grey-darker text-2xl">{post.description}</p>
                                        </div>
                                        <div className="flex items-center">
                                            <img className="w-14 h-14 rounded-full mr-4" src={post.userAvatar} alt="" />
                                            <div>
                                                <p className="text-black leading-none text-lg mb-0">{post.userName}</p>
                                                <p className="text-grey-dark text-lg mb-0">{moment(post.updatedAt).format('MM/DD/YYYY')}</p>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                        
                        ))
                    }
                </Col>
                <Col data-aos="zoom-out-left"  xs={{span: 24}} sm={{span: 24}} md={{span: 24}} lg={{span: 6}} lx={{span: 6}}>
                    
                </Col>
            </Row>
        </div>
        </>
    );
}

export default Posts;
